sys_variable_value.value:
// =============================================================
// Author: 		    Ronald Lob
// Script Type:     [Workflow] Customer Onboarding - Foundation - User
// Script Name:	    Customer Onboarding - Foundation - User to Group
// Script Version:	1.0
// Script Desc:	    Adds a single User to a single Group 
// ==========
// Revisions:
// ==========				
// 21/02/2018 - [Version 1.0][Ronald Lob] Development.
// =============================================================
	
Main();

function Main(){
	// Main control function
	
	gs.info('[Customer Onboarding][WF][User] Begin Request Add User to Group Workflow.');
	
	var user = new User();
	var group = new Group();
	var gr_u = new GlideRecord();
	var gr_g = new GlideRecord();
	gr_u = user.Get_SysID(current.variables.User);
	gr_g = group.Get_SysID(current.variables.Group);


	if (gr_u.isValidRecord() == true && gr_g.isValidRecord() == true){
		gs.info('[Customer Onboarding][WF][User] Found User: ' + gr_u.sys_id + ' and Group: ' + gr_g.sys_id);
		user.AddUserToGroup(gr_u.sys_id,gr_g.sys_id);
	} else {
		gs.info('[Customer Onboarding][WF][User] User or Group not found. User: ' + current.variables.User + ' Group: ' + current.variables.Group);
		
	}
}

