function Main(){
	// Main control function
	
	gs.info('[Customer Onboarding][WF][Department] Begin Request Edit Department Workflow.');
	
	var department = new Department();
	var value = current.variables.Value;
	var field_to_update = current.variables.Field_To_Update;
	var gr = new GlideRecord();
	gr = department.Get_SysID(current.variables.Department);
	
	// Lets find out if we're updating "Value" or a Reference field
	if (current.variables.Field_To_Update == "company") {
		value = current.variables.Company; 
	} else if (current.variables.Field_To_Update == "cost_center") {
		value = current.variables.Cost_Center;
	} else if (current.variables.Field_To_Update == "parent") {
		value = current.variables.Parent;
	} else if (current.variables.Field_To_Update == "department_head") {
		value = current.variables.Department_Head;
	} else if (current.variables.Field_To_Update == "primary_contact") {
		value = current.variables.Primary_Contact;
	}

	if (gr.isValidRecord() == false){
		gs.info('[Customer Onboarding][WF][Department] Department with the sys_id of ' + gr.sys_id + ' not found.');				
	} else {
		gs.info('[Customer Onboarding][WF][Department] Department with the sys_id of ' + gr.sys_id + ' found, ready for editing.');
		var bool = department.Update(gr.sys_id,field_to_update,value);

		if (bool == true) {
			gs.info('[Customer Onboarding][WF][Department] Department: ' + gr.sys_id + ' successfully updated. Field: ' + field_to_update + ' set to: ' + value);
		} else {
			gs.info('[Customer Onboarding][WF][Department] ' + gr.sys_id + ' could not be edited.');	
		}
	}
}

