	gr = location.Get_Name(current.variables.Name);

		if (gr.isValidRecord() == true){
			gs.info('[Customer Onboarding][WF][Location] Location with the name of ' + current.variables.Name + ' already exists in ServiceNow. Exiting.');				
		} else {
			gs.info('[Customer Onboarding][WF][Location] Location with the name of ' + current.variables.Name + ' does not already exist in ServiceNow. Creating a new one.');
			var bool = location.Create(current.variables.Name, current.variables.Parent, current.variables.Country, current.variables.State_Province, current.variables.City, current.variables.Street, current.variables.Contact, current.variables.Company, current.variables.Latitude, current.variables.Longitude, current.variables.Stock_Room);
			
			if (bool == true) {
				gs.info('[Customer Onboarding][WF][Location] ' + current.variables.Name + ' successfully created.');
			} else {
				gs.info('[Customer Onboarding][WF][Location] ' + current.variables.Name + ' could not be created.');	
			}
		}
}

