// =============================================================
// Author: 		    Ronald Lob
// Script Type:     [Workflow] Customer Onboarding - Foundation - Support Group
// Script Name:	    Customer Onboarding - Foundation - Support Group
// Script Version:	1.4
// Script Desc:	    Validates existing Support Group off a new Service Catalog request item. If not existing, creates new otherwise updates existing  
// ==========
// Revisions:
// ==========
// 11/12/2017 - [Version 1.0][Ronald Lob]  	Development.
// 26/02/2018 - [Version 1.1][Ronald Lob]  	Changed Create call parameters with new requirements.
//                                          Added the ability to assign members to the Group using the List Collector.
// 12/04/2018 - [Version 1.2][Ronald Lob]  	Add roles to new Support Group.
// 17/04/2018 - [Version 1.3][Ronald Lob]  	Updated roles to new Support Group to match Spark Custom.
// 15/10/2018 - [Version 1.4][Ronald Lob]  	Add domain visibility to new Support Group
// =============================================================
	
Main();

function Main(){
	// Main control function
	
	gs.info('[Customer Onboarding][WF][Group] Begin Request New Support Group Workflow.');
	
	var group = new Group();
	var gr = new GlideRecord();
	gr = group.Get_Name(current.variables.Name, current.variables.Company);

		if (gr.isValidRecord() == true){
			gs.info('[Customer Onboarding][WF][Group] Support Group with the name of ' + current.variables.Name + ' already exists in ServiceNow. Exiting.');				
		} else {
			gs.info('[Customer Onboarding][WF][Group] Support Group with the name of ' + current.variables.Name + ' does not already exist in ServiceNow. Creating a new one.');
			var gr_g = group.Create(true, current.variables.Cost_Center, current.variables.Default_Assignee, current.variables.Description, current.variables.Group_Email, current.variables.Manager, current.variables.Name, current.variables.Parent, "itil", current.variables.Company, current.variables.Labour);
			
			if (gr_g.isValidRecord()) {
				gs.info('[Customer Onboarding][WF][Group] Group: ' + gr_g.name + ' successfully created.');
				
					// Here we need to loop through the list collector and add the users specified within the list collector to the group.
					var str = current.variables.Members.toString();
					var array = str.split(",");

					for (var i = 0; i < array.length; i++) {
						gs.info('[Customer Onboarding][WF][Group] Adding User ' + array[i] + ' to the group ' + gr_g.name);
						group.AddUserToGroup(array[i],gr_g.sys_id);
					}
				
				   // Awesome, users have been added (hopefully). Lets assign roles to this group now. 
				   var roles = ["knowledge_submitter","service_desk_request","service_desk_incident","change_requestor"];
					   
				   for (var j = 0; j < roles.length; j++) {
						gs.info('[Customer Onboarding][WF][Group] Adding role ' + roles[j] + ' to the group ' + gr_g.name);
						group.AddRoleToGroup(roles[j],gr_g.sys_id);
				   }
				
				   // Adding COMMON visibility to this Group if the Company = Spark New Zealand Limited
				   if (gr_g.company == "d9057fc1db98d7005070326f7c961926") {
					   gs.info('[Customer Onboarding][WF][Group] Adding domain COMMON to the group ' + gr_g.name);
					   group.AddVisibilityToGroup("COMMON",gr_g.sys_id);
				   }
				
			} else {
				gs.info('[Customer Onboarding][WF][Group] Group: ' + current.variables.Name + ' could not be created.');	
			}
		}
}

