// =============================================================
// Author: 		    Ronald Lob
// Script Type:     [Workflow] Customer Onboarding - Foundation - Company
// Script Name:	    Customer Onboarding - Foundation - Set Core Values
// Script Version:	1.00
// Script Desc:	    Sets the Description field of the RITM with variable values. 
//                  This is useful due to the way Spark approves RITM via email.
// ==========
// Revisions:
// ==========				
// 16/10/2018 - [Version 1.0][Ronald Lob]  	Development.
// =============================================================
var description = '';

if (current.variables.Company) {
	description = "Company: " + current.variables.Company.name;
}

if (current.opened_by) {
	description = description + "\nRequested By: " + current.opened_by.name;
}

if (current.variables.Name) {
	description = description + "\nSupport Group Name: " + current.variables.Name;
}

if (current.variables.Group_Email) {
	description = description + "\nGroup Email Address: " + current.variables.Group_Email;
}

if (current.variables.Manager) {
	description = description + "\nManager: " + current.variables.Manager.name;
}

if (current.variables.User) {
	description = description + "\nUser: " + current.variables.User.name;
}

if (current.variables.Group) {
	description = description + "\nGroup: " + current.variables.Group.name;
}

if (current.variables.Justification) {
	description = description + "\nJustification: " + current.variables.Justification;
}



current.description = description;

// =============================================================
// Author: 		    Ronald Lob
// Script Type:     [Workflow] Customer Onboarding - Foundation - Company
// Script Name:	    Customer Onboarding - Foundation - Set Core Values
// Script Version:	1.00
// Script Desc:	    Sets the Description field of the RITM with variable values. 
//                  This is useful due to the way Spark approves RITM via email.
// ==========
// Revisions:
// ==========				
// 16/10/2018 - [Version 1.0][Ronald Lob]  	Development.
// =============================================================
var description = '';

if (current.variables.Company) {
	description = "Company: " + current.variables.Company.name;
}

if (current.opened_by) {
	description = description + "\nRequested By: " + current.opened_by.name;
}

if (current.variables.Name) {
	description = description + "\nSupport Group Name: " + current.variables.Name;
}

if (current.variables.Group_Email) {
	description = description + "\nGroup Email Address: " + current.variables.Group_Email;
}

if (current.variables.Manager) {
	description = description + "\nManager: " + current.variables.Manager.name;
}

if (current.variables.User) {
	description = description + "\nUser: " + current.variables.User.name;
}

if (current.variables.Group) {
	description = description + "\nGroup: " + current.variables.Group.name;
}

if (current.variables.Justification) {
	description = description + "\nJustification: " + current.variables.Justification;
}



current.description = description;

// Set the variable 'answer' to a comma-separated list of group ids or an array of group ids to add as approvers.
//
// For example:
//       var answer = [];
//       answer.push('id1');
//       answer.push('id2');

// Set the variable 'answer' to '', 'approved' or 'rejected' to indicate the overall approval status for this approval.
//
// This script is responsible for setting the approval state for each group that is part of this approval activity before
// returning the overall approval state for all of the group(s).
//
// When called, the following variables are available to the script:
//
//    Overall for all of the groups that are part of this approval activity:
//        counts.total = total number of groups that are part of this approval
//        counts.approved = # of groups that approved so far
//        counts.rejected = # of groups that rejected so far
//        counts.requested = # of groups that are pending approval
//        counts.not_requested = # of groups that are not pending approval
//        counts.not_required = # of groups that approval is not required
//
//    And for each group:
//        groups[group_id].total = total number of users that are part of this group's approval
//        groups[group_id].approved = # of users that approved so far
//        groups[group_id].rejected = # of users that rejected so far
//        groups[group_id].requested = # of users that are pending approval
//        groups[group_id].not_requested = # of users that are not pending approval
//        groups[group_id].not_required = # of users that approval is not required
//
//        groups[group_id].approvalIDs[state] = array of user ids that are at the specified approval state
//
//        Note: Iterate the groups using:
//            for (var id in groups) {
//                 var group = groups[id];
//                 ...  group.total ...
//            }

// Set the variable 'answer' to '', 'approved' or 'rejected' to indicate the overall approval status for this approval.
//
// This script is responsible for setting the approval state for each group that is part of this approval activity before
// returning the overall approval state for all of the group(s).
//
// When called, the following variables are available to the script:
//
//    Overall for all of the groups that are part of this approval activity:
//        counts.total = total number of groups that are part of this approval
//        counts.approved = # of groups that approved so far
//        counts.rejected = # of groups that rejected so far
//        counts.requested = # of groups that are pending approval
//        counts.not_requested = # of groups that are not pending approval
//        counts.not_required = # of groups that approval is not required
//
//    And for each group:
//        groups[group_id].total = total number of users that are part of this group's approval
//        groups[group_id].approved = # of users that approved so far
//        groups[group_id].rejected = # of users that rejected so far
//        groups[group_id].requested = # of users that are pending approval
//        groups[group_id].not_requested = # of users that are not pending approval
//        groups[group_id].not_required = # of users that approval is not required
//
//        groups[group_id].approvalIDs[state] = array of user ids that are at the specified approval state
//
//        Note: Iterate the groups using:
//            for (var id in groups) {
//                 var group = groups[id];
//                 ...  group.total ...
//            }

// Set the variable 'answer' to a comma-separated list of group ids or an array of group ids to add as approvers.
//
// For example:
//       var answer = [];
//       answer.push('id1');
//       answer.push('id2');

 // Set the variable 'answer' to a comma-separated list of user ids and/or group ids or an array of user/group ids to add as approvers.
//
// For example:
       answer = [];

	   if (current.variables.Group.manager){
		   answer.push(current.variables.Group.manager.sys_id.toString());
	   } else {
		   answer.push(current.variables.Group.sys_id.toString());
	   }
