// =============================================================
// Author: 		    Ronald Lob
// Script Type:     [Workflow] Customer Onboarding - Foundation - Company
// Script Name:	    Customer Onboarding - Foundation - Company
// Script Version:	1.10
// Script Desc:	    Edits an existing Company based on provided field name and field value  
// ==========
// Revisions:
// ==========				
// 28/11/2017 - [Version 1.0][Ronald Lob]  	Development.
// 13/12/2017 - [Version 1.1][Ronald Lob]  	Changed Update field parses to accomodate the UI Script work.
// =============================================================
	
Main();

function Main(){
	// Main control function
	
	gs.info('[Customer Onboarding][WF][Company] Begin Request Edit Company Workflow.');
	
	var company = new Company();
	var value = current.variables.Value;
	var field_to_update = current.variables.Field_To_Update;
	var gr = new GlideRecord();
	gr = company.Get_SysID(current.variables.Company);
	
	// Lets find out if we're updating "Value" or a Reference field
	if (current.variables.Field_To_Update == "contact") {
		value = current.variables.Contact; 
	} else if (current.variables.Field_To_Update == "location") {
		value = current.variables.Location;
	} else if (current.variables.Field_To_Update == "parent") {
		value = current.variables.Parent;
	} else if (current.variables.Field_To_Update == "company_type" && current.variables.Customer == "true") {
		field_to_update = "customer";
		value = true;
	} else if (current.variables.Field_To_Update == "company_type" && current.variables.Manufacturer == "true") {
		field_to_update = "manufacturer";
		value = true;
	} else if (current.variables.Field_To_Update == "company_type" && current.variables.Vendor == "true") {
		field_to_update = "vendor";
		value = true;
	}

	if (gr.isValidRecord() == false){
		gs.info('[Customer Onboarding][WF][Company] Company with the sys_id of ' + gr.sys_id + ' not found.');				
	} else {
		gs.info('[Customer Onboarding][WF][Company] Company with the sys_id of ' + gr.sys_id + ' found, ready for editing.');
		var bool = company.Update(gr.sys_id,field_to_update,value);

		if (bool == true) {
			gs.info('[Customer Onboarding][WF][Company] Company: ' + gr.sys_id + ' successfully updated. Field: ' + field_to_update + ' set to: ' + value);
		} else {
			gs.info('[Customer Onboarding][WF][Company] ' + gr.sys_id + ' could not be edited.');	
		}
	}
}

