// =============================================================
// Author: 		    Ronald Lob
// Script Type:     [Workflow] Customer Onboarding - Foundation - User
// Script Name:	    Customer Onboarding - Foundation - Cost Center
// Script Version:	1.00
// Script Desc:	    Validates existing User off a new Service Catalog request item. If not existing, creates new otherwise updates existing  
// ==========
// Revisions:
// ==========				
// 16/02/2017 - [Version 1.0][Ronald Lob] Development.
// =============================================================
	
Main();

function Main(){
	// Main control function
	
	gs.info('[Customer Onboarding][WF][User] Begin Request New User Workflow.');
	
	var user = new User();

	var gr = user.Create(true, current.variables.User_ID, null, current.variables.Employee_Number, current.variables.First_Name, current.variables.Last_Name, false, current.variables.Company, current.variables.Department, null, current.variables.Location, null, current.variables.Email_Address, current.variables.Phone, current.variables.Manager, current.variables.Cost_Center);
								   
	if (gr.isValidRecord() == true) {

		// Here we need to loop through the list collector and add this new user to their groups.
		var str = current.variables.Support_Groups.toString();
		var array = str.split(",");

		for (var i = 0; i < array.length; i++) {
			gs.info('[Customer Onboarding][WF][User] Adding ' + gr.user_name + ' to the group ' + array[i]);
			user.AddUserToGroup(gr.sys_id,array[i]);
		}

	} else {
		gs.info('[Customer Onboarding][WF][User] ' + current.variables.User_ID + '(' + current.variables.First_Name + ' ' + current.variables.Last_Name + ') could not be created.');	
	}
			
}

