// =============================================================
// Author: 		    Ronald Lob
// Script Type:     [Workflow] Customer Onboarding - Foundation - User
// Script Name:	    Customer Onboarding - Foundation - Remove Group
// Script Version:	1.0
// Script Desc:	    Deactivates an Existing Group (we will let business rules decide if this record is to be deleted) 
// ==========
// Revisions:
// ==========				
// 27/02/2018 - [Version 1.0][Ronald Lob] Development.
// =============================================================
	
Main();

function Main(){
	// Main control function
	
	gs.info('[Customer Onboarding][WF][Group] Begin Request Remove Group Workflow.');
	
	var group = new Group();
	var gr = new GlideRecord();
	gr = group.Get_SysID(current.variables.Group);

	if (gr.isValidRecord() == false) {
		gs.info('[Customer Onboarding][WF][Group] Group with the sys_id of ' + gr.sys_id + ' not found.');				
	} else {
		gs.info('[Customer Onboarding][WF][Group] Group with the sys_id of ' + gr.sys_id + ' found, ready to deactivate.');
		var bool = group.Deactivate(gr.sys_id);

		if (bool == true) {
			gs.info('[Customer Onboarding][WF][User] User: ' + gr.sys_id + ' successfully deactivated.');
		} else {
			gs.info('[Customer Onboarding][WF][User] ' + gr.sys_id + ' could not be deactivated.');	
		}
	}
}

