// =============================================================
// Author: 		    Ronald Lob
// Script Type:     [Workflow] Customer Onboarding - Foundation - Company
// Script Name:	    Customer Onboarding - Foundation - Company
// Script Version:	1.00
// Script Desc:	    Deletes an existing Company and cascades the delete to child items  
// ==========
// Revisions:
// ==========				
// 28/11/2017 - [Version 1.0][Ronald Lob]  	Development.
// =============================================================
	
Main();

function Main(){
	// Main control function
	
	gs.info('[Customer Onboarding][WF][Company] Begin Request Delete Company Workflow.');
	
	var company = new Company();
	var gr = new GlideRecord();
	gr = company.Get_SysID(current.variables.Company);

		if (gr.isValidRecord() == false){
			gs.info('[Customer Onboarding][WF][Company] Company with the sys_id of ' + gr.sys_id + ' not found.');				
		} else {
			gs.info('[Customer Onboarding][WF][Company] Company with the sys_id of ' + gr.sys_id + ' found, ready to delete.');
			var bool = company.Delete(gr.sys_id);
			
			if (bool == true) {
				gs.info('[Customer Onboarding][WF][Company] Company: ' + gr.sys_id + ' successfully deleted.');
			} else {
				gs.info('[Customer Onboarding][WF][Company] ' + gr.sys_id + ' could not be deleted.');	
			}
		}
}

