// =============================================================
// Author: 		    Ronald Lob
// Script Type:     [Workflow] Customer Onboarding - Foundation - Company
// Script Name:	    Customer Onboarding - Foundation - Company
// Script Version:	1.00
// Script Desc:	    Validates existing Company off a new Service Catalog request item. If not existing, creates new otherwise updates existing  
// ==========
// Revisions:
// ==========				
// 23/11/2017 - [Version 1.0][Ronald Lob]  	Development.
// =============================================================
	
Main();

function Main(){
	// Main control function
	
	gs.info('[Customer Onboarding][WF][Company] Begin Request New Company Workflow.');
	
	var company = new Company();
	var gr = new GlideRecord();
	gr = company.Get_Name(current.variables.Name);

		if (gr.isValidRecord() == true){
			gs.info('[Customer Onboarding][WF][Company] Company with the name of ' + current.variables.Name + ' already exists in ServiceNow. Exiting.');				
		} else {
			gs.info('[Customer Onboarding][WF][Company] Company with the name of ' + current.variables.Name + ' does not already exist in ServiceNow. Creating a new one.');
			var sys_id = company.Create(current.variables.Name,current.variables.Contact,current.variables.Latitude,current.variables.Longitude,current.variables.Customer,current.variables.Vendor,current.variables.Manufacturer,current.variables.Location,current.variables.Parent,current.variables.Website);
			
			if (sys_id) {
				gs.info('[Customer Onboarding][WF][Company] ' + current.variables.Name + ' successfully created with sys_id = ' + sys_id);
			} else {
				gs.info('[Customer Onboarding][WF][Company] ' + current.variables.Name + ' could not be created.');	
			}
		}
}

