// Script Name:	    Customer Onboarding - Foundation - Cost Center
// Script Version:	1.00
// Script Desc:	    Validates existing User off a new Service Catalog request item. If not existing, creates new otherwise updates existing  
// ==========
// Revisions:
// ==========				
// 16/02/2017 - [Version 1.0][Ronald Lob] Development.
// =============================================================
	
Main();

function Main(){
	// Main control function
	
	gs.info('[Customer Onboarding][WF][User] Begin Request New User Workflow.');
	
	var user = new User();
	var gr = new GlideRecord();
	gr = user.ValidateDuplicate(current.variables.User_ID, current.variables.Employee_Number, current.variables.First_Name, current.variables.Last_Name, current.variables.Email_Address);

		if (gr.isValidRecord() == true){
			gs.info('[Customer Onboarding][WF][User] User with the name of ' + current.variables.User_ID + ' already exists in ServiceNow. Exiting.');				
		} else {
			gs.info('[Customer Onboarding][WF][User] User with the name of ' + current.variables.User_ID + '(' + current.variables.First_Name + ' ' + current.variables.Last_Name + ') does not already exist in ServiceNow. Creating a new one.');
			var bool = user.Create(true, current.variables.User_ID, null, current.variables.Employee_Number, current.variables.First_Name, current.variables.Last_Name, false, current.variables.Company, current.variables.Department, null, current.variables.Location, null, current.variables.Email_Address, current.variables.Phone, current.variables.Manager, current.variables.Cost_Center);
								   
			if (bool == true) {
				gs.info('[Customer Onboarding][WF][User] ' + current.variables.User_ID + '(' + current.variables.First_Name + ' ' + current.variables.Last_Name + ') successfully created.');
			} else {
				gs.info('[Customer Onboarding][WF][User] ' + current.variables.User_ID + '(' + current.variables.First_Name + ' ' + current.variables.Last_Name + ') could not be created.');	
			}
			
		// Here we need to loop through the list collector and add this new user to their groups. 
		}
}

