// =============================================================
// Author: 		    Ronald Lob
// Workflow:        Customer Onboaridng - Foundation
// Script Name:	    Customer Onboarding - Foundation - New Company
// Script Version:	1.00
// Script Desc:	    On approval of a Request Item, we create the relavent underlying data elements in accordance with predefined business rules. 
// ==========
// Revisions:
// ==========				
// 23/11/2017 - [Version 1.0][Ronald Lob]  	Development.
// =============================================================
	
Main();

function Main(){
	// Main control function
	
	gs.info('Begin Request New Company Workflow.');

	var exists = ValidateExisting();

		if (exists == true){
			gs.info('Company with the name of ' + current.variables.Name + ' already exists in ServiceNow.');
			gs.info('To update an existing Company, please select the correct Catalog Item from the Foundation menu.');
			
		} else if (exists == false) {
			gs.info('Company with the name of ' + current.variables.Name + ' does not already exists in ServiceNow. Creating a new one.');
			var newSysID = CreateNewCompany();
			gs.info(current.variables.Name + ' (' + newSysID + ') successfully created.');
		}
}

function ValidateExisting() {
	// Validate data to see if data already exists based entirely on a name search
	
	gs.info('Validate Company data to see if data already exists.');
	
	gr = new GlideRecord('core_company');
	gr.addQuery('name','=',current.variables.Name);
	gr.query();

	var count = gr.getRowCount();	

	if (count == 0) {
		gs.info('Validation has not found an existing Company record.');
		return false;
	} else {
		gs.info('Validation found an existing Company record.');
		return true;
	}
}

function CreateNewCompany() {
	// Create a new Company in ServiceNow
	
	gs.info('Create a new Company in ServiceNow.');
	
	gr = new GlideRecord('core_company');
	gr.initialize();
	gr.name = current.variables.Name;
	gr.contact = current.variables.Contact;
	gr.latitude = current.variables.Latitude;
	gr.longitude = current.variables.Longitude;
	gr.customer = current.variables.Customer;
	gr.vendor = current.variables.Vendor;
	var sysId = gr.insert();
	
	gs.info('New Company record created successfully: ' + sysId);
	
	return sysId;
}

